    public static class HtmlToPdf
    {
        private static Stream inputHtml;

        public static void Generate(Stream pInputHtmlFilePath, string pOutputFilePath,
            string pHeaderLeftValue, string pTitle, bool setLandscapePage = false)
        {
            inputHtml = pInputHtmlFilePath;
            Document doc = !setLandscapePage ? new Document(PageSize.A4, 40, 40, 80, 80)
                : new Document(PageSize.A4.Rotate(), 40, 40, 80, 80);
            PdfWriter pdfWriter = PdfWriter.GetInstance(doc, new FileStream(pOutputFilePath, FileMode.Create));
            pdfWriter.PageEvent = new ITextEvents(pHeaderLeftValue, pTitle);
            doc.Open();
            //FileStream cssFileStream = new FileStream("sample.css", FileMode.Open);
            TransformHtml2Pdf(doc, pdfWriter, new SampleTestImageProvider(),
                new XMLWorkerFontProvider(), null);
            //cssFileStream.Close();
            doc.Close();
            
        }

        public static void GenerateInvoice(Stream pInputHtmlFilePath, string pOutputFilePath,
            string pHeaderLeftValue, string pTitle, bool setLandscapePage = false)
        {
            inputHtml = pInputHtmlFilePath;
            Document doc = !setLandscapePage ? new Document(PageSize.A4, 20, 20, 70, 30)
                : new Document(PageSize.A4.Rotate(), 20, 20, 70, 30);
            PdfWriter pdfWriter = PdfWriter.GetInstance(doc, new FileStream(pOutputFilePath, FileMode.Create));
            pdfWriter.PageEvent = new ITextEvents(pHeaderLeftValue, pTitle);
            doc.Open();
            //FileStream cssFileStream = new FileStream("sample.css", FileMode.Open);
            TransformHtml2Pdf(doc, pdfWriter, new SampleTestImageProvider(),
                new XMLWorkerFontProvider(), null);
            //cssFileStream.Close();
            doc.Close();
        }

        private static void TransformHtml2Pdf(Document doc, PdfWriter pdfWriter, IImageProvider imageProvider,
            IFontProvider fontProvider, Stream cssFile)
        {
            try
            {
                CssFilesImpl cssFiles = new CssFilesImpl();
                if (cssFile == null)
                    cssFile =
                        typeof(XMLWorker).Assembly.GetManifestResourceStream("iTextSharp.tool.xml.css.default.css");
                cssFiles.Add(XMLWorkerHelper.GetCSS(cssFile));
                StyleAttrCSSResolver cssResolver = new StyleAttrCSSResolver(cssFiles);
                HtmlPipelineContext hpc;

                if (fontProvider != null)
                    hpc = new HtmlPipelineContext(new CssAppliersImpl(fontProvider));
                else
                    hpc = new HtmlPipelineContext(null);

                var tagProcessors = (DefaultTagProcessorFactory)Tags.GetHtmlTagProcessorFactory();
                tagProcessors.RemoveProcessor(HTML.Tag.IMG);
                tagProcessors.AddProcessor(HTML.Tag.IMG, new CustomImageTagProcessor());

                hpc.SetAcceptUnknown(true).AutoBookmark(true).SetTagFactory(tagProcessors);
                HtmlPipeline htmlPipeline = new HtmlPipeline(hpc, new PdfWriterPipeline(doc, pdfWriter));
                IPipeline pipeline = new CssResolverPipeline(cssResolver, htmlPipeline);
                XMLWorker worker = new XMLWorker(pipeline, true);
                XMLParser xmlParse = new XMLParser(true, worker, Encoding.GetEncoding("UTF-8"));
                xmlParse.Parse(inputHtml);
            }
            catch (Exception exc)
            {
                throw exc;
            }
        }
    }

